<?phpinclude_once(dirname(__FILE__)."/ressources/class.template-admin.inc");if(!isset($GLOBALS["CLASS_SOCKETS"])){if(!class_exists("sockets")){include_once("/usr/share/artica-postfix/ressources/class.sockets.inc");}$GLOBALS["CLASS_SOCKETS"]=new sockets();}include_once(dirname(__FILE__)."/ressources/class.ip2host.inc");if(isset($_GET["verbose"])){$GLOBALS["VERBOSE"]=true;ini_set('display_errors', 1);ini_set('error_reporting', E_ALL);ini_set('error_prepend_string',null);ini_set('error_append_string',null);}if(isset($_GET["query"])){query();exit;}if(isset($_GET["query-js"])){query_js();exit;}if(isset($_GET["query-popup"])){query_popup();exit;}if(isset($_POST["FROM"])){query_save();exit;}if(isset($_GET["export-database-js"])){export_database_js();exit();}if(isset($_GET["export-db-show-js"])){export_database_show_js();exit;}if(isset($_GET["export-db-show"])){export_database_show();exit;}if(isset($_POST["export-database"])){admin_tracks("Confirm Exporting Proxy statistics database");exit;}if(isset($_GET["file-uploaded"])){file_uploaded();exit;}if(isset($_GET["import-database-js"])){import_database_js();exit;}if(isset($_GET["import-database-popup"])){import_database_popup();exit;}page();function query_js():bool{    $tpl        = new template_admin();    $page       = CurrentPageName();    $tpl->js_dialog1("{build_query}","$page?query-popup=yes",650);    return true;}function export_database_show_js(){    $tpl        = new template_admin();    $page       = CurrentPageName();    $tpl->js_dialog1("{database}","$page?export-db-show=yes",650);    return true;}function export_database_show():bool{    $ARROOTR=ARTICA_ROOT;    $pname="statscom.gz";    $fname="ressources/conf/upload/$pname";    $tcompress="$ARROOTR/$fname";    if(!is_file($tcompress)){return false;}    $size=filesize($tcompress);    $size=FormatBytes($size/1024);    $date=date("Y-m-d H:i:s",filemtime($tcompress));    $filedown="		<div class='center' style='margin:15px'>		<a href='$fname'>		<img src='img/file-compressed-128.png' class='img-rounded'>		</a><br>		<a href='$fname'><small>$pname ($size)</small></a><br>		<a href='$fname'><small>$date</small></a>		</div>    ";    echo $filedown;    return true;}function import_database_js(){    $page=CurrentPageName();    $tpl=new template_admin();    $filename=$_GET["import-database-js"];    if($filename<>null){        if(!isset($_GET["confirm"])) {            $jsAfter = "Loadjs('$page?import-database-js=$filename&confirm=yes');";            $tpl->js_dialog_confirm_action("{restore} $filename", "restore", "$filename", $jsAfter);            return;        }    }    $tpl->js_dialog1("{restore} {container} $filename","$page?import-database-popup=$filename",550);}function file_uploaded(){    header("content-type: application/x-javascript");    $page=CurrentPageName();    $filename=$_GET["file-uploaded"];    admin_tracks("Importing Proxy statistics container $filename");    $ARRAY["PROGRESS_FILE"]=PROGRESS_DIR."/exportdb.progress";    $ARRAY["LOG_FILE"]=PROGRESS_DIR."/exportdb.progress.txt";    $ARRAY["CMD"]="statscom.php?import-backup=".urlencode($filename);    $ARRAY["TITLE"]="{importing} $filename";    $ARRAY["AFTER"]="LoadAjax('stats-com-data','$page?query=yes');dialogInstance1.close();";    $prgress=base64_encode(serialize($ARRAY));    $jsrestart="Loadjs('fw.progress.php?content=$prgress&mainid=restore-container-upload')";    echo $jsrestart;}function import_database_popup(){    $page       =   CurrentPageName();    $tpl        = new template_admin();    $filename   = $_GET["restore-popup"];    $html[]="<div id='restore-container-upload'></div>";    if($filename==null){        $html[]=$tpl->div_explain("{restore_statscom_container_explain}");        $html[]="<div class='center' style='margin:30px'>";        $html[]=$tpl->form_button_upload("{upload}",$page,null,"AsWebStatisticsAdministrator");        $html[]="</div>";    }else{        $ARRAY["PROGRESS_FILE"]=PROGRESS_DIR."/exportdb.progress";        $ARRAY["LOG_FILE"]=PROGRESS_DIR."/exportdb.progress.txt";        $ARRAY["CMD"]="statscom.php?import-backup=".urlencode($filename);        $ARRAY["TITLE"]="{importing} $filename";        $ARRAY["AFTER"]="LoadAjax('stats-com-data','$page?query=yes');dialogInstance1.close();";        $prgress=base64_encode(serialize($ARRAY));        $jsrestart="Loadjs('fw.progress.php?content=$prgress&mainid=restore-container-upload')";        $html[]="<script>$jsrestart;</script>";    }    echo $tpl->_ENGINE_parse_body($html);}function export_database_js():bool{    $tpl        = new template_admin();    $page       = CurrentPageName();    $ARRAY["PROGRESS_FILE"]=PROGRESS_DIR."/exportdb.progress";    $ARRAY["LOG_FILE"]=PROGRESS_DIR."/exportdb.progress.txt";    $ARRAY["TITLE"]="{exporting}";    $ARRAY["CMD"]="statscom.php?export-db=yes";    $ARRAY["AFTER"]="Loadjs('$page?export-db-show-js=yes');LoadAjax('stats-com-data','$page?query=yes');";    $prgress=base64_encode(serialize($ARRAY));    $applyjs="Loadjs('fw.progress.php?content=$prgress&mainid=stats-com-data')";    $tpl->js_confirm_execute("{export_statistics_database_ask}","export-database","yes",$applyjs);    return true;}function query_popup(){    $tpl=new template_admin();    $page=CurrentPageName();    $q=new postgres_sql();    $ARRAY_IP=array();    $ARRAY_USER=array();    $ARRAY_MAC=array();    if(!$q->CREATE_STATSCOM()) {        echo $tpl->div_error($q->mysql_error);    }    $results=$q->QUERY_SQL("SELECT zdate FROM statscom_days GROUP BY zdate ORDER BY zdate DESC");    if(!$q->ok){echo $tpl->div_error($q->mysql_error);}    $DATES[date("Y-m-d")]=$tpl->time_to_date(time());    $DATES[date("Y-m-d",strtotime( '-1 days' ))]=$tpl->time_to_date(strtotime( '-1 days' ));    while ($ligne = pg_fetch_assoc($results)) {        $time=strtotime($ligne["zdate"]);        $DATES[$ligne["zdate"]]=$tpl->time_to_date($time);    }    $results=$q->QUERY_SQL("SELECT username,ipaddr,mac FROM statscom_users GROUP BY username,ipaddr,mac ORDER BY username,ipaddr,mac");    if(!$q->ok){echo $tpl->div_error($q->mysql_error);}    while ($ligne = pg_fetch_assoc($results)) {        $username=$ligne["username"];        $ipaddr=$ligne["ipaddr"];        $mac=$ligne["mac"];        if($mac<>"00:00:00:00:00:00"){            $ARRAY_MAC[$mac]=$mac;        }        if($username<>null){            $ARRAY_USER[$username]=$username;        }        if($ipaddr<>null){            $ARRAY_IP[$ipaddr]=$ipaddr;        }    }    for($i=0;$i<24;$i++){        $h=$i;        if($i<10){$h="0$i";}        $h="$h";        $TIMES[$h]=$h;    }    for($i=0;$i<60;$i++){        $h=$i;        if($i<10){$h="0$i";}        $h="$h";        $MINS[$h]=$h;    }    $form[]=$tpl->field_section("{from_date}");    $form[]=$tpl->field_array_hash($DATES,"FROM","nonull:{from_date}",$_SESSION["STATSCOM_DAY"]["FROM"]);    $tb=explode(":",$_SESSION["STATSCOM_DAY"]["FROMH"]);    $form[]=$tpl->field_array_hash($TIMES,"FROM_HOUR","nonull:{from_time}",$tb[0]);    $form[]=$tpl->field_array_hash($MINS,"FROM_MIN","nonull:{minutes}",$tb[1]);    $form[]=$tpl->field_section("{to_date}");    $tb=explode(":",$_SESSION["STATSCOM_DAY"]["TOH"]);    $form[]=$tpl->field_array_hash($DATES,"TO","nonull:{to_date}",$_SESSION["STATSCOM_DAY"]["TO"]);    $form[]=$tpl->field_array_hash($TIMES,"TO_HOUR","nonull:{from_time}",$tb[0]);    $form[]=$tpl->field_array_hash($MINS,"TO_MIN","nonull:{minutes}",$tb[1]);    $form[]=$tpl->field_section("{websites}");    $q=new postgres_sql();    $catz=new mysql_catz();    $CATEGORIES=array();    $results=$q->QUERY_SQL("SELECT category FROM statscom_websites GROUP BY category");    if(!$q->ok){echo $tpl->div_error($q->mysql_error);}    while ($ligne = pg_fetch_assoc($results)) {        $CATEGORIES[$ligne["category"]]=$catz->CategoryIntToStr($ligne["category"]);    }    if(!isset($_SESSION["STATSCOM_DAY"]["CATEGORY"])){        $_SESSION["STATSCOM_DAY"]["CATEGORY"]="";    }    $form[] = $tpl->field_array_hash($CATEGORIES, "CATEGORY", "{category}", $_SESSION["STATSCOM_DAY"]["CATEGORY"]);    $form[]=$tpl->field_section("{members}");    if(count($ARRAY_USER)>0) {        $form[] = $tpl->field_array_hash($ARRAY_USER, "USER", "{member}", $_SESSION["STATSCOM_DAY"]["USER"]);    }else{        $tpl->field_hidden("USER","");    }    $form[]=$tpl->field_array_hash($ARRAY_IP,"IP","{ipaddr}",$_SESSION["STATSCOM_DAY"]["IP"]);    if(count($ARRAY_MAC)>0) {        $form[] = $tpl->field_array_hash($ARRAY_MAC, "MAC",            "{MAC}", $_SESSION["STATSCOM_DAY"]["MAC"]);    }else{        $tpl->field_hidden("MAC","");    }    if(!isset($_SESSION["STATSCOM_DAY"]["LIMIT"])){$_SESSION["STATSCOM_DAY"]["LIMIT"]=250;}    $form[]=$tpl->field_numeric("SIZE","{size_up_to} (KB)",$_SESSION["STATSCOM_DAY"]["SIZE"]);    $form[]=$tpl->field_numeric("LIMIT","{max_items}",$_SESSION["STATSCOM_DAY"]["LIMIT"]);    echo $tpl->form_outside("{build_query}",$form,null,"{launch}","LoadAjax('stats-com-data','$page?query=yes');","AsWebStatisticsAdministrator",true);}function query_save(){    $tpl=new template_admin();    $tpl->CLEAN_POST();    $_SESSION["STATSCOM_DAY"]["FROM"]=$_POST["FROM"];    $_SESSION["STATSCOM_DAY"]["TO"]=$_POST["TO"];    $_SESSION["STATSCOM_DAY"]["FROMH"]=$_POST["FROM_HOUR"].":".$_POST["FROM_MIN"];    $_SESSION["STATSCOM_DAY"]["TOH"]=$_POST["TO_HOUR"].":".$_POST["TO_MIN"];    $_SESSION["STATSCOM_DAY"]["USER"]=$_POST["USER"];    $_SESSION["STATSCOM_DAY"]["IP"]=$_POST["IP"];    $_SESSION["STATSCOM_DAY"]["MAC"]=$_POST["MAC"];    $_SESSION["STATSCOM_DAY"]["SIZE"]=$_POST["SIZE"];    $_SESSION["STATSCOM_DAY"]["LIMIT"]=$_POST["LIMIT"];    $_SESSION["STATSCOM_DAY"]["CATEGORY"]=$_POST["CATEGORY"];}function page(){    $tpl    = new template_admin();    $page   = CurrentPageName();    $html[]="<div class=\"btn-group\" data-toggle=\"buttons\" style='margin-top: 10px'>";    $html[]=$tpl->button_label_table("{build_query}",            "Loadjs('$page?query-js=yes')", "fas fa-filter","AsWebStatisticsAdministrator");    $html[]=$tpl->button_label_table("{refresh}",        "LoadAjax('stats-com-data','$page?query=yes')", "fas fa-sync-alt","AsWebStatisticsAdministrator");//<i class="fas fa-file-export"></i>    if($GLOBALS["CLASS_SOCKETS"]->CORP_LICENSE()){        $html[]=$tpl->button_label_table("{export}",            "Loadjs('$page?export-database-js=yes')", "fas fa-file-export","AsWebStatisticsAdministrator");        $html[]=$tpl->button_label_table("{import}",            "Loadjs('$page?import-database-js=')", "fas fa-file-import","AsWebStatisticsAdministrator");            }    $html[]="</div>";    $html[]="<div id='stats-com-data' ></div>";    $html[]="<script>LoadAjax('stats-com-data','$page?query=yes');</script>";    echo $tpl->_ENGINE_parse_body($html);}function query(){    $tpl=new template_admin();    $catz=new mysql_catz();    $q=new postgres_sql();    $q->CREATE_STATSCOM();    $t=time();    $TRCLASS=null;$WHEREU=null;$WHEREM=null;$WHEREI=null;$WHERES=null;$WEREC=null;    //86340 = 24H    // 172740  =    if(!isset($_SESSION["STATSCOM_DAY"]["FROM"])){        $_SESSION["STATSCOM_DAY"]["FROM"]=date("Y-m-d");    }    if(!isset($_SESSION["STATSCOM_DAY"]["TO"])){        $_SESSION["STATSCOM_DAY"]["TO"]=date("Y-m-d");    }    if(!isset($_SESSION["STATSCOM_DAY"]["FROMH"])){        $_SESSION["STATSCOM_DAY"]["FROMH"]="00:00";    }    if(!isset($_SESSION["STATSCOM_DAY"]["TOH"])){        $_SESSION["STATSCOM_DAY"]["TOH"]="23:59";    }    if(!isset($_SESSION["STATSCOM_DAY"]["LIMIT"])){        $_SESSION["STATSCOM_DAY"]["LIMIT"]="250";    }    if(intval($_SESSION["STATSCOM_DAY"]["LIMIT"])==0){        $_SESSION["STATSCOM_DAY"]["LIMIT"]=250;    }    $time1=strtotime( $_SESSION["STATSCOM_DAY"]["FROM"]." ". $_SESSION["STATSCOM_DAY"]["FROMH"]);    $time2=strtotime($_SESSION["STATSCOM_DAY"]["TO"]." ". $_SESSION["STATSCOM_DAY"]["TOH"]);    $seconds=$time2-$time1;    if($seconds<0){       echo  $tpl->div_error("{please_define_dates_in_correct_order}");        return;    }    if($seconds<259150){        $TABLE_SELECTED="statscom";        $date1=$_SESSION["STATSCOM_DAY"]["FROM"]." ". $_SESSION["STATSCOM_DAY"]["FROMH"];        $date2=$_SESSION["STATSCOM_DAY"]["TO"]." ". $_SESSION["STATSCOM_DAY"]["TOH"];        $tt2=date("Y-m-d",strtotime($date2));        $TITLES[]="{from} " .$tpl->time_to_date(strtotime($date1),true);        if($tt2==date("Y-m-d")) {            $TITLES[] = "{to} {today} " . $tpl->time_to_date(strtotime($date2), true);        }else{            $TITLES[] = "{to} " . $tpl->time_to_date(strtotime($date2), true);        }    }else{        $TABLE_SELECTED="statscom_days";        $date1=$_SESSION["STATSCOM_DAY"]["FROM"];        $date2=$_SESSION["STATSCOM_DAY"]["TO"];        $tt2=date("Y-m-d",strtotime($date2));        $TITLES[]="{from} " .$tpl->time_to_date(strtotime($date1),false);        if($tt2==date("Y-m-d")) {            $TITLES[] = "{to} {today} " . $tpl->time_to_date(strtotime($date2), false);        }else{            $TITLES[] = "{to} " . $tpl->time_to_date(strtotime($date2), false);        }    }    $TITLES[]="(".distanceOfTimeInWords(strtotime($date1),strtotime($date2)).")";    if($_SESSION["STATSCOM_DAY"]["USER"]<>null){        $TITLES[]="{and} {member} {$_SESSION["STATSCOM_DAY"]["USER"]}";        $WHEREU=" AND username='{$_SESSION["STATSCOM_DAY"]["USER"]}'";    }    if($_SESSION["STATSCOM_DAY"]["IP"]<>null){        $TITLES[]="{and} {ipaddr} {$_SESSION["STATSCOM_DAY"]["IP"]}";        $WHEREI=" AND ipaddr='{$_SESSION["STATSCOM_DAY"]["IP"]}'";    }    if($_SESSION["STATSCOM_DAY"]["MAC"]<>null){        $TITLES[]="{and} {MAC} {$_SESSION["STATSCOM_DAY"]["MAC"]}";        $WHEREM=" AND mac='{$_SESSION["STATSCOM_DAY"]["MAC"]}'";    }    if( intval($_SESSION["STATSCOM_DAY"]["SIZE"])>0){        $size_q=round(intval( $_SESSION["STATSCOM_DAY"]["SIZE"])*1024);        $WHERES=" $TABLE_SELECTED.size > $size_q AND";    }    if(is_numeric($_SESSION["STATSCOM_DAY"]["CATEGORY"])){        $catz=new mysql_catz();        $TITLES[]="{and} {category} ".$catz->CategoryIntToStr($_SESSION["STATSCOM_DAY"]["CATEGORY"]);        $WEREC=" AND statscom_websites.category={$_SESSION["STATSCOM_DAY"]["CATEGORY"]}";    }    $html[]="<H2 style='margin-top: 10px'>".@implode(" ",$TITLES)."<h3><i class=\"fas fa-file-csv\"></i>&nbsp; <a href='ressources/logs/web/statscom.csv'>statscom.csv</a></h3></H2>";    $sql="SELECT SUM($TABLE_SELECTED.hits) as hits,     SUM($TABLE_SELECTED.size) as size,    $TABLE_SELECTED.username,    $TABLE_SELECTED.ipaddr,    $TABLE_SELECTED.mac,    $TABLE_SELECTED.zdate,    statscom_websites.sitename,    statscom_websites.siteid,    statscom_websites.category       FROM $TABLE_SELECTED,statscom_websites WHERE{$WHERES}    statscom_websites.siteid=$TABLE_SELECTED.siteid AND    $TABLE_SELECTED.zdate >='$date1' AND  $TABLE_SELECTED.zdate <= '$date2'{$WHEREU}{$WHEREI}{$WHEREM}{$WEREC}    GROUP BY $TABLE_SELECTED.zdate,$TABLE_SELECTED.username,$TABLE_SELECTED.ipaddr,$TABLE_SELECTED.mac,statscom_websites.sitename, statscom_websites.siteid,statscom_websites.category ORDER BY $TABLE_SELECTED.zdate    LIMIT {$_SESSION["STATSCOM_DAY"]["LIMIT"]}";    $results=$q->QUERY_SQL($sql);    if(!$q->ok){        echo  $tpl->_ENGINE_parse_body($html);        echo  $tpl->div_error("{$seconds}s<br>".$q->mysql_error."<br>$sql");        return;    }    $csvpath=dirname(__FILE__)."/ressources/logs/web/statscom.csv";    $html[]="    <table id='table-$t' class=\"footable table table-stripped\"     data-page-size=\"100\" data-paging=\"true\">";    $html[]="<thead>";    $html[]="<tr>";    $html[]="<th data-sortable=true class='text-capitalize' data-type='text'>{date}</th>";    $html[]="<th data-sortable=true class='text-capitalize' data-type='text'>{sitename}</th>";    $html[]="<th data-sortable=true class='text-capitalize' data-type='text'>{category}</th>";    $html[]="<th data-sortable=true class='text-capitalize' data-type='text' nowrap>{member}</th>";    $html[]="<th data-sortable=true class='text-capitalize' data-type='text'>{MAC}</th>";    $html[]="<th data-sortable=true class='text-capitalize' data-type='text'>{ipaddr}</th>";    $html[]="<th data-sortable=true class='text-capitalize' data-type='text'>{size}</th>";    $html[]="<th data-sortable=true class='text-capitalize' data-type='text'>{hits}</th>";    $html[]="</tr>";    $html[]="</thead>";    $html[]="<tbody>";    $td1=$tpl->table_td1prc_Left();    $tdfree=$tpl->table_tdfree();    $fpcsv = fopen($csvpath, 'w');    fputcsv($fpcsv, array("date", "Site", 'Categoy', 'Member','Mac','IP','Size','Hits'));    while ($ligne = pg_fetch_assoc($results)) {        if ($TRCLASS == "footable-odd ") {            $TRCLASS = null;        } else {            $TRCLASS = "footable-odd ";        }        $hits=$tpl->FormatNumber($ligne["hits"]);        $size=FormatBytes($ligne["size"]/1024);        $date=$ligne["zdate"];        $sitename=$ligne["sitename"];        $category_id=$ligne["category"];        $category=$catz->CategoryIntToStr($category_id);        $ipaddr=$ligne["ipaddr"];        $username=$ligne["username"];        $mac=$ligne["mac"];        $siteid=$ligne["siteid"];        $resolveIP2HOST=intval($GLOBALS["CLASS_SOCKETS"]->GET_INFO("resolveIP2HOST"));        if($resolveIP2HOST==1){            $host= new ip2host($ipaddr);            $ipaddr=$host->output;        }        fputcsv($fpcsv, array($date, $sitename, $category, $username,$mac,$ipaddr,$ligne["size"],"$hits"));        $macenc=urlencode($mac);        $category=$tpl->td_href($category,"{statistics}","Loadjs('fw.statscom.browse.categories.php?category=$category_id')");        $mac=$tpl->td_href($mac,"{statistics}","Loadjs('fw.statscom.browse.users.php?field=mac&value=$macenc')");        $ipaddr=$tpl->td_href($ipaddr,"{statistics}","Loadjs('fw.statscom.browse.users.php?field=ipaddr&value=$ipaddr')");        $username=$tpl->td_href($username,"{statistics}","Loadjs('fw.statscom.browse.users.php?field=username&value=$username')");        $sitename=$tpl->td_href($sitename,null,"Loadjs('fw.statscom.browse.website.php?siteid=$siteid')");        $html[]="<tr style='vertical-align:middle' class='$TRCLASS'>";        $html[]="<td $td1>$date</td>";        $html[]="<td $tdfree>$sitename</td>";        $html[]="<td $td1>$category</td>";        $html[]="<td $td1>$username</td>";        $html[]="<td $td1>$mac</td>";        $html[]="<td $td1>$ipaddr</td>";        $html[]="<td $td1>$size</td>";        $html[]="<td $td1>$hits</td>";        $html[]="</tr>";    }    fclose($fpcsv);    $html[]="</tbody>";    $html[]="<tfoot>";    $html[]="<tr>";    $html[]="<td colspan='8'>";    $html[]="<ul class='pagination pull-right'></ul>";    $html[]="</td>";    $html[]="</tr>";    $html[]="</tfoot>";    $html[]="</table>";    $html[]="<script> NoSpinner();\n".@implode("\n",$tpl->ICON_SCRIPTS)."$(document).ready(function() { $('#table-$t').footable({ \"filtering\": { \"enabled\": true }, \"sorting\": { \"enabled\": true },\"paging\": { \"size\": {$GLOBALS["FOOTABLE_PSIZE"]} } } ) }); </script>";    echo $tpl->_ENGINE_parse_body(@implode("\n", $html));}